import{a as n}from"./index-CDcGWq3o.js";const h=n([{title:"route.blogs",slug:"/blogs"},{title:"route.healthcare",slug:"/blogs/healthcare"},{title:"route.service",slug:"/blogs/service"},{title:"route.sale",slug:"/blogs/sale"},{title:"route.information",slug:"/blogs/information"}]),s={healthcare:[{title:"LẦM TƯỞNG KHI CHO THÚ CƯNG ĂN THỨC ĂN NÓNG",slug:"/blogs/detail/lam-tuong-khi-cho-thu-cung-an-thuc-an-nong",imageUrl:"https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/B%C3%A0i%20vi%E1%BA%BFt/Cover%20(1).png",desc:"Vì chúng ta thích những bữa ăn ấm áp thì không có nghĩa là thú cưng cũng như thế. Sự thật là với cấu trúc sinh học của chó mèo, các Boss rất dễ bị tổn thương đường tiêu hóa khi ăn thức ăn nóng!"},{title:"Tác dụng phụ của tiêm phòng vắc-xin ở chó mèo",slug:"/blogs/detail/tac-dung-phu-cua-tiem-phong-vacxin-o-cho-meo",imageUrl:"https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/vaccine%20ch%C3%B3%20m%C3%A8o/thumb-02-02.jpg",desc:"Tác dụng phụ của tiêm phòng ở chó mèo là điều hiển nhiên sẽ xảy ra. Bạn đừng quá lo lắng sau khi tiêm ngừa cho thú cưng sẽ gặp những biểu hiện lạ."},{title:"Các bước trước trong và sau khi tắm cho chó",slug:"/blogs/detail/cac-buoc-truoc-trong-va-sau-khi-tam-cho-cho",imageUrl:"https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/THUMB.jpg",desc:"Tắm cho chó nghe qua ai cũng nghĩ là việc đơn giản. Nhưng bạn có chắc việc tắm cho chó của bạn đúng cách không? Nếu kỹ năng người chủ tốt sẽ có thể giúp chó cưng thấy thoải mái, loại bỏ các mầm bệnh tiềm ẩn … nhưng nếu không sẽ còn gây hại cho chó của bạn nữa đấy. "},{title:"Hachiko - Tượng đài về lòng trung thành",slug:"/blogs/detail/hachiko--tuong-dai-ve-long-trung-thanh",imageUrl:"https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/hachiko-facts.jpg",desc:"Chắc hẳn các bạn đều biết đến chú chó Hachiko nổi tiếng đến từ Nhật Bản. Vậy hôm nay PETPRO sẽ kể lại chuyện đầy đủ của Hachiko nhé"}],service:[{title:"XÉT NGHIỆM NHANH, CHẨN ĐOÁN ĐÚNG VỚI CÔNG NGHỆ PCR TẠI PETPRO",slug:"/blogs/detail/xet-nghiem-nhanh-chan-doan-dung-voi-cong-nghe-pcr-tai-petpro",imageUrl:"https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/B%C3%A0i%20vi%E1%BA%BFt/31452768a2c6719828d7.jpg",desc:"Là bệnh viện thú y đầu tiên tại Việt Nam có chứng chỉ ISO, PETRO hiểu rõ tầm quan trọng của việc nâng cấp quy trình chẩn đoán."}],sale:[{title:"COMBO TIEM VACXIN TOAN DIEN CHI CON 790.000 VND",slug:"/blogs/detail/combo-tiem-vacxin-toan-dien-chi-con-790000-vnd",imageUrl:"https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/295798628_3289447681334166_6256429223842253994_n.jpg",desc:""},{title:"NGẬP TRÀN ƯU ĐÃI NHÂN DỊP KHAI TRƯƠNG TRỤ SỞ MỚI 550 LŨY BÁN BÍCH",slug:"/blogs/detail/ngap-tran-uu-dai-nhan-dip-khai-truong-tru-so-moi-550-luy-ban-bich",imageUrl:"https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/z3489437080012_1ba93a81fd5c5c521794ad670e859012.jpg",desc:""},{title:"TAM SAY THANG 07 - UU DAI BONG TAY",slug:"/blogs/detail/tam-say-thang-07-uu-dai-bong-tay",imageUrl:"https://admin.petpro.com.vn/images/uploaded/99k.jpg",desc:""},{title:"MUA 2 ĐƯỢC 4 - Sen tin được không ?????",slug:"/blogs/detail/mua-2-duoc-4--sen-tin-duoc-khong-",imageUrl:"https://admin.petpro.com.vn/images/uploaded/mua2duoc4.jpg",desc:""}],information:[{title:"ThS.BS Thai Thi My Hanh nhận bằng khen thưởng của Chi hội Thú y thú nhỏ Việt Nam",slug:"/blogs/detail/thsbs-thai-thi-my-hanh-nhan-bang-khen-thuong-cua-chi-hoi-thu-y-thu-nho-viet-nam",imageUrl:"https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/69521f3004c2c69c9fd3.jpg",desc:""},{title:"THÔNG BÁO TUYỂN DỤNG NHÂN SỰ NĂM 2022",slug:"/blogs/detail/thong-bao-tuyen-dung-nhan-su",imageUrl:"https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/z3499918271967_eceb81f29693f0dce935803b148b3d88.jpg",desc:""},{title:"TƯNG BỪNG KHAI TRƯƠNG TRỤ SỞ MỚI TẠI SỐ 550 LŨY BÁN BÍCH",slug:"/blogs/detail/tung-bung-khai-truong-tru-so-moi-tai-so-550-luy-ban-bich",imageUrl:"https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/z3493835663827_e7cdcfa9c19a74a76095c4b6d9ec49b4.jpg",desc:""},{title:"Thông báo chuyển địa điểm chi nhánh Nguyễn Thị Tú",slug:"/blogs/detail/thong-bao-chuyen-dia-diem-chi-nhanh-nguyen-thi-tu",imageUrl:"https://admin.petpro.com.vn/images/uploaded/thu-y-petpro-nguyen-thi-tu.jpg",desc:""}]},i=n([{title:"LẦM TƯỞNG KHI CHO THÚ CƯNG ĂN THỨC ĂN NÓNG",slug:"lam-tuong-khi-cho-thu-cung-an-thuc-an-nong",type:"route.healthcare",content:'<div class="w-full drop-shadow-[0_5px_10px_rgba(0,0,0,0.5)]"> <img class="w-full rounded-[5px] pinterest-img" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/B%C3%A0i%20vi%E1%BA%BFt/Cover%20(1).png" alt="LẦM TƯỞNG KHI CHO THÚ CƯNG ĂN THỨC ĂN NÓNG"> </div> <div style="margin-top: 20px; margin-bottom: 20px;"> <h4 class="text-[15px] leading-[24px] font-medium-700 text-alabaster-5070 pb-[10px] text-italic text-justify-content"> Vì chúng ta thích những bữa ăn ấm áp thì không có nghĩa là thú cưng cũng như thế. Sự thật là với cấu trúc sinh học của chó mèo, các Boss rất dễ bị tổn thương đường tiêu hóa khi ăn thức ăn nóng!</h4> </div> <div class="paragraph-content"> <p><strong>1. Vì sao thú cưng không nên ăn đồ nóng?</strong></p> <p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Với cấu tạo cơ thể hoàn toàn khác với người, môi, lưỡi và họng của chó, mèo không có cơ chế chống lại nhiệt độ cao. Vì vậy, khi ăn phải thức ăn nóng, niêm mạc họng rất dễ bị kích ứng và tổn thương.&nbsp;</p> <p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Đặc biệt, nếu tiếp xúc trong thời gian dài, thức ăn nóng có thể gây ảnh hưởng xấu đến sức khỏe của thú cưng. Chúng ta có thể hiểu đơn giản là: đồ ăn nóng làm bỏng đường tiêu hóa và dẫn đến nôn mửa. Đây chính là nguyên nhân dẫn đến tăng tiết axit làm suy yếu lớp niêm mạc bảo vệ và tạo nên các tổn thương dạ dày, thực quản. Về sau, tình trạng viêm loét dạ dày có thể diễn tiến nghiêm trọng hơn, khiến Boss chán nản khi ăn và gây đau đớn trong sinh hoạt thường ngày.</p> <p style="text-align: center;"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/B%C3%A0i%20vi%E1%BA%BFt/fa5ea67ceb613e3f6770.jpg"></p> <p style="text-align: center;"><em>Thức ăn nóng không giúp bữa ăn thêm ngon miệng, ngược lại còn ảnh hưởng xấu đến sức khoẻ Boss cưng.</em></p> <p style="text-align: center;">&nbsp;</p> <p><strong>2. Nhiệt độ lý tưởng cho thú cưng ăn ngon miệng là bao nhiêu?</strong></p> <p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Thực tế, thức ăn “mát” sẽ không khiến Boss phàn nàn vì nhiệt độ thức ăn phù hợp với Boss nên rơi vào khoảng 18-30°C. Điều này đảm bảo thức ăn không gây tổn thương niêm mạc họng, ruột và dạ dày của chúng. Bạn cũng cần lưu ý không để thức ăn quá lạnh vì như thế sẽ làm giảm hương vị bữa ăn và chậm quá trình tiêu hóa, giảm sự hấp thụ dinh dưỡng ở các Boss.</p> <p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Nói như vậy không có nghĩa Boss buộc phải ăn “đồ nguội lạnh” trong quãng đời còn lại. Khi các Boss đang bệnh, bạn có thể tăng nhiệt độ thức ăn lên tầm 38°C để làm thức ăn dậy mùi, giúp các bé ăn ngon miệng hơn trong lúc cơ thể còn đang yếu ớt.&nbsp;</p> <p style="margin-left: 40px;"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/B%C3%A0i%20vi%E1%BA%BFt/f813fa39b724627a3b35.jpg"></p> <p style="text-align: center;"><em>Điều quan trọng khi hâm đồ ăn là Sen hãy nhớ để thức ăn nguội đến nhiệt độ phù hợp trước khi dâng lên cho các Boss</em></p> <p>&nbsp;</p> <p><strong>3. Những lưu ý quan trọng khác khi cho thú cưng ăn</strong></p> <p>Ngoài lưu ý về nhiệt độ, có những thức ăn rất quen thuộc với con người nhưng với chó, mèo lại gây hại, thậm chí có thể khiến các bé tử vong. Bạn hãy chú ý đến vài nguyên tắc cần tránh dưới đây:&nbsp;</p> <ul> <li>Thức ăn cay: Tương tự như thức ăn nóng, đồ cay có thể gây kích thích và tổn thương hệ tiêu hóa từ đó ảnh hưởng xấu đến sức khỏe các Boss. Và hiển nhiên, không có chó, mèo nào lại có khẩu vị thích ăn cay cả!</li> <li>Thức ăn vị mặn, thức ăn cho người: Bạn có bất ngờ khi biết thức ăn của người nêm nếm đầy đủ gia vị, đặc biệt là đồ đóng hộp lại có hại cho chó, mèo không? Vị mặn sẽ làm các Boss dễ rụng lông. Nguy hiểm hơn đó là nó có thể gây ra các bệnh lý sỏi thận, suy thận, suy bàng quang,... Vì vậy nếu bạn nấu thức ăn cho Boss, để đảm bảo sức khỏe thì nhớ đừng nêm nếm gia vị.&nbsp;</li> <li>Xương động vật: Xương rất dễ làm các Boss nghẹn, nhiễm trùng và rối loạn tiêu hóa. Đó là chưa kể đến việc ăn xương còn gây ra nguy cơ gãy răng, tổn thương miệng cho thú cưng. Vì vậy, nếu bạn muốn cho thú cưng ăn xương, hãy tham khảo ý kiến của bác sĩ hoặc chuyên gia dinh dưỡng thú y để biết loại xương an toàn và phù hợp cho loài thú cưng của bạn.&nbsp;&nbsp;</li> <li>Các thức ăn gây ngộ độc cho thú cưng: Sô-cô-la, các quả nho, bơ, hồng, đào, mận, thức ăn chứa caffeine và cồn,... đều là những món ăn cực kỳ quen thuộc với con người nhưng lại gây nguy hiểm cho các Boss.</li> </ul> <p><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/B%C3%A0i%20vi%E1%BA%BFt/8f90d7b29aaf4ff116be.jpg"></p> <p style="text-align: center;"><em>Bạn hãy tìm hiểu thật kỹ về các loại thức ăn có hại cho chó, mèo để chăm sóc Boss thật tốt</em></p> <p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Như vậy, có những loại thức ăn tưởng chừng là khoái khẩu với con người nhưng lại có tác hại nguy hiểm đến thú cưng. Hy vọng những kiến thức trong bài viết trên sẽ giúp các bạn có thêm kinh nghiệm để chăm sóc các Boss cưng thật khỏe mạnh.&nbsp;</p> <p>Hãy thường xuyên theo dõi PETPRO vì chúng tôi sẽ liên tục cập nhật thêm nhiều thông tin hữu ích bạn nhé!</p> <p>&nbsp;</p> ',imageUrl:"https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/B%C3%A0i%20vi%E1%BA%BFt/Cover%20(1).png",desc:"Vì chúng ta thích những bữa ăn ấm áp thì không có nghĩa là thú cưng cũng như thế. Sự thật là với cấu trúc sinh học của chó mèo, các Boss rất dễ bị tổn thương đường tiêu hóa khi ăn thức ăn nóng!"},{title:"Tác dụng phụ của tiêm phòng vắc-xin ở chó mèo",slug:"tac-dung-phu-cua-tiem-phong-vacxin-o-cho-meo",type:"route.healthcare",content:'<div class="w-full drop-shadow-[0_5px_10px_rgba(0,0,0,0.5)]"> <img class="w-full rounded-[5px] pinterest-img" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/vaccine%20ch%C3%B3%20m%C3%A8o/thumb-02-02.jpg" alt="Tác dụng phụ của tiêm phòng vắc-xin ở chó mèo"> </div><div style="margin-top: 20px; margin-bottom: 20px;"> <h4 class="text-[15px] leading-[24px] font-medium-700 text-alabaster-5070 pb-[10px] text-italic text-justify-content"> Tác dụng phụ của tiêm phòng ở chó mèo là điều hiển nhiên sẽ xảy ra. Bạn đừng quá lo lắng sau khi tiêm ngừa cho thú cưng sẽ gặp những biểu hiện lạ. </h4> </div><div class="paragraph-content"> <p style="text-align:justify"><span style="font-size:16px">Hậu tiêm ngừa cho chó mèo, bạn sẽ bắt gặp những dấu hiệu bất thường từ các bé: nôn, tiêu chảy, mệt mỏi, lờ đờ,… nguyên nhân là vì sao?&nbsp;Tác dụng phụ của tiêm phòng ở chó mèo&nbsp;gồm những triệu chứng như thế nào, và làm sao để khắc phục được tình trạng này? Bài viết này sẽ giúp bạn đi tìm câu trả lời và giới thiệu đến bạn những bệnh cần tiêm ngừa vaccine cho chó mèo trong suốt cuộc đời của chúng.&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/vi%C3%AAm%20tu%E1%BB%B5/1-03.jpg"></span></p> <h2 style="text-align:justify"><strong><span style="font-size:20px">Tại sao cần tiêm ngừa vaccine cho chó mèo?&nbsp;</span></strong></h2> <p style="text-align:justify"><span style="font-size:16px">Tiêm ngừa vaccine cho chó mèo&nbsp;là nhiệm vụ bắt buộc mà chủ nuôi phải thực hiện khi đón các bé về sống cùng. Đây là việc làm vô cùng quan trọng nhằm đảm bảo cho thú cưng có một sức khỏe tốt, hạn chế tối đa rủi ro chúng sẽ mắc phải một số bệnh truyền nhiễm, bệnh không có thuốc đặc trị,…</span></p> <p style="text-align:justify"><span style="font-size:16px">Bên cạnh đó, khi&nbsp;tiêm ngừa cho chó&nbsp;hoặc&nbsp;tiêm ngừa cho mèo, những kháng thể trong vacxin sẽ giúp cơ thể chúng tạo ra sức đề kháng chống lại những căn bệnh nguy hiểm, làm giảm đi khả năng gây bệnh của những virus có hại. Vì vậy, việc&nbsp;tiêm ngừa cho chó mèo&nbsp;là điều cần thiết ngay khi chúng được 1,5 tháng – 2 tháng tuổi.&nbsp;</span></p> <h2 style="text-align:justify"><span style="font-size:20px"><strong>Nguyên nhân dẫn đến tác dụng phụ của tiêm phòng ở chó mèo?</strong></span></h2> <p style="text-align:justify"><span style="font-size:16px">Hệ miễn dịch của mỗi vật nuôi sẽ có sức mạnh và độ cân bằng khác nhau, vì vậy, khi vắc-xin được đưa vào cơ thể sẽ tạo ra những phản ứng khác nhau.&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px">Nguyên nhân chủ yếu dẫn đến những tác dụng phụ khác nhau từ nhẹ đến nặng hơn là do cơ địa của từng bé nhạy cảm đối với 1 hoặc 1 vài thành phần của thuốc.<br> Nguyên nhân thứ 2 là do tâm lý lo sợ tự có của các bé sau khi được tiêm mà bạn sẽ khó có thể quan sát bằng mắt thường.<br> Nguyên nhân thứ ba dẫn đến một vài&nbsp;tác dụng phụ của tiêm phòng ở chó mèo&nbsp;là sức đề kháng của các bé còn non nớt, cơ thể nhiễm 1 số bệnh lý vô tình phản ứng cùng thời điểm với vaccine được tiêm vào.</span></p> <p style="text-align:justify"><span style="font-size:16px">Bạn đừng quá lo lắng, tất cả những điều này báo hiệu cho thấy vắc-xin đã kích hoạt trong cơ thể chúng và đang bảo vệ để chống lại.&nbsp;</span></p> <h2 style="text-align:justify"><span style="font-size:20px"><strong>Những tác dụng phụ nào thường xuất hiện sau tiêm phòng ở chó mèo?&nbsp;</strong></span></h2> <p style="text-align:justify"><span style="font-size:16px">Sau khi cơ thể của cún – miu đã bắt đầu tiếp nhận lượng vaccine được tiêm vào, bạn sẽ dễ dàng nhìn thấy chúng có nhiều thay đổi lạ thường. Tùy thuộc vào hệ thống miễn dịch trong cơ thể mà sẽ có những&nbsp;phản ứng phụ sau tiêm phòng&nbsp;khác nhau ở chó mèo, mức độ từ nhẹ đến nặng cũng sẽ theo đó mà diễn ra.</span></p> <p style="text-align:justify"><span style="font-size:16px">a/ Tác dụng phụ thông thường sau khi tiêm ngừa vaccine cho chó mèo:&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/vi%C3%AAm%20tu%E1%BB%B5/1-04.jpg" style="width:100%"></span></p> <p style="text-align:justify"><span style="font-size:16px">Vết tiêm bị sưng&nbsp;làm thay đổi sắc tố da<br> Cơ thể mệt mỏi, uể oải<br> Nhiều bé có dấu hiệu sốt nhẹ<br> Sổ mũi<br> Chán ăn, ăn uống không ngon miệng&nbsp;<br> Rụng lông</span></p> <p style="text-align:justify"><span style="font-size:16px">Trong đó dấu hiệu&nbsp;chó mèo bị sưng sau khi tiêm&nbsp;thường dễ nhìn thấy nhất, lúc này hãy dùng tay xoa nhẹ vào vết tiêm của chúng để giảm bớt cơn đau, cho các bé cảm giác dễ chịu, thoải mái hơn. Những biểu hiện này thường xuất hiện từ 1 – 3 ngày và sẽ không kéo dài quá lâu.&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px">b/ Tác dụng phụ nghiêm trọng sau tiêm phòng ở chó mèo:</span></p> <p style="text-align:justify"><span style="font-size:16px">Một số bé do sức đề kháng yếu hoặc không đủ sức đề kháng sẽ có những phản ứng dữ dội hơn một chút:&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px">Ngứa dữ dội, nổi mẩn<br> Sụt cân<br> Thay đổi hành vi, chạy nhảy vô thức<br> Phát ban<br> Tiêu chảy<br> Nôn mửa<br> Thở gấp, thở khò khè<br> Nổi mụn nhỏ li ti ở những vùng da mỏng quanh khu vực tiêm</span></p> <p style="text-align:justify"><span style="font-size:16px">Một vài triệu chứng khác diễn ra nặng hơn như viêm da, tạm thời không đi được,… lúc này cần đưa bé trở lại phòng khám hoặc bệnh viện ngay để theo dõi và điều trị, tránh trường hợp xấu nhất xảy đến.</span></p> <p style="text-align:justify"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/vi%C3%AAm%20tu%E1%BB%B5/1-05.jpg" style="width:100%"></span></p> <p style="text-align:justify"><span style="font-size:16px">c/ Tác dụng phụ cực kỳ nghiêm trọng và nguy hiểm sau tiêm phòng ở chó mèo:</span></p> <p style="text-align:justify"><span style="font-size:16px">Nếu như cơ thể bé yêu nhà bạn không đủ sức để kháng lại vaccine, gây tác dụng ngược cực kỳ nghiêm trọng đến cơ thể như:&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px">Sốc phản ứng thuốc<br> Co giật<br> Dị tật<br> Có hiện tượng tự cắn chính mình<br> Tử vong</span></p> <p style="text-align:justify"><span style="font-size:16px">Thông thường, trước khi&nbsp;tiêm ngừa vaccine cho chó mèo, các bé đều sẽ được kiểm tra sức khỏe để bảo đảm an toàn trước khi chích. Vì vậy, những phản ứng cực kỳ nghiêm trọng sẽ ít gặp phải. Nếu kết quả kiểm tra trước tiêm không gặp nhiều trục trặc, việc tiêm phòng sẽ diễn ra cực kỳ suôn sẻ.&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px">Chăm sóc thú cưng sau tiêm ngừa</span></p> <p style="text-align:justify"><span style="font-size:16px">Pet Pro mách bạn 5 điều cần lưu ý cho thú cưng sau khi đã được tiêm ngừa. Chủ nuôi không nên lơ là để hạn chế tối đa các trường hợp sau khi tiêm nhé!</span></p> <p style="text-align:justify"><span style="font-size:16px">Không tắm cho bé trong vòng 5 ngày sau tiêm phòng, vì thời gian này bé thường sốt nhẹ. Không để mắc mưa hoặc phơi nắng quá lâu.<br> Không cho chó mèo tiếp xúc với các bé khác không rõ nguồn gốc hoặc đang mang bệnh. Vì bé chưa tạo đủ miễn dịch bảo hộ.<br> Không cho hoặc hạn chế cho các bé đi chơi xa để tránh thời tiết xấu (mưa, gió, trời âm u hoặc nắng nóng trên 32 độ C) và không hoạt động mạnh (huấn luyện, chạy đua, thi đấu) trong vòng 14 ngày sau tiêm chủng.<br> Không cho thú cưng sử dụng các thức ăn dầu mỡ, chất béo, thức ăn khó tiêu (trứng lộn, xúc xích, phô mai, sữa, nội tạng, gặm xương..). Sử dụng các thực phẩm dễ tiêu, được là nhuyễn, xay mềm giàu dinh dưỡng trong 10-14 ngày sau tiêm ngừa để tạo miễn dịch hoàn hảo. Hãy nhớ luôn cung cấp đủ nước cho các bé bạn nhé.<br> Nên thường xuyên kiểm tra và xoa nhẹ vị trí tiêm chích (do vết tiêm thường bị sưng cứng). Nếu phát hiện bé có những triệu chứng bất thường như: sốt cao, lừ đừ, bỏ ăn, ói, tiêu chảy,… bạn nên mang bé đến bệnh viện để kiểm tra lại.</span></p> <p style="text-align:justify"><span style="font-size:16px">Lưu ý : Vaccine sẽ có miễn dịch sau 14 ngày kể từ ngày tiêm, bạn cần chăm sóc bé thật kĩ vì bé cũng có thể phát bệnh trong giai đoạn này do chưa đạt miễn dịch bảo hộ hoặc sốc phản vệ với vacxin được tiêm.</span></p> <p style="text-align:justify"><span style="font-size:16px">Những bệnh cần tiêm ngừa cho mèo?&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/vaccine%20ch%C3%B3%20m%C3%A8o/vaccine%20cho%20meo-04.jpg" style="width:100%"></span></p> <p style="text-align:justify"><span style="font-size:16px">Khi đến các trung tâm, phòng khám hay bệnh viện thú y uy tín, bạn sẽ được giới thiệu sơ lược về quy trình tiêm ngừa, thời gian tái chủng, và một số bệnh cần&nbsp;tiêm ngừa cho mèo.&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px">Bệnh dại – là bệnh hiện nay chưa có thuốc chữa trị hiệu quả , có thể lây cho người nếu vô tình bị cào/cắn trúng.<br> Bệnh suy giảm bạch cầu – FPV (Feline panleucopenia)/Distemper : loại bệnh nguy hiểm không có triệu chứng rõ rệt, lây lan cao và nhanh<br> Bệnh viêm mũi khí quản trên mèo – FRV (Feline Rhinotracheitis Viral): là nguyên nhân gây ra bệnh đường hô hấp.<br> Bệnh do Calicivirus – FCV (Feline Calicivirus): là loại bệnh có tính mãn tính, khó chữa và là yếu tố mở đường cho các loại virus, vi khuẩn khác.<br> Bệnh ung thư bạch cầu mèo – FeLV (Feline Leukemia Virus): là một loại bệnh phổ biến làm số lượng bạch cầu giảm nghiêm trọng, làm cho mèo có sức sống kém chết lần mòn.<br> Bệnh viêm đường hô hấp trên (Feline Chlamydiosis): Bệnh này có thể gây ra nhiều biến chứng nghiêm trọng như mù mắt, khó thở mãn tính nếu mèo không được điều trị kịp thời.<br> Bệnh viêm phúc mạc – FIP (Feline Infectious Peritonitis): là một bệnh do virus thuộc chủng Coronavirus gây ra ở mèo.Mèo bị nhiễm Coronavirus thường không có triệu chứng trong thời gian nhiễm virus ban đầu, mèo của mọi lứa tuổi vẫn có thể mắc bệnh.</span></p> <p style="text-align:justify"><span style="font-size:16px">Những bệnh cần tiêm ngừa cho chó?&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px">Tương tự như mèo, các bé cún cũng sẽ có một số bệnh cần phòng tránh nhất định vì hơn 80% chúng sẽ mắc phải nếu không được chích ngừa đầy đủ.&nbsp;</span></p> <p style="text-align:justify"><span style="font-size:16px">Bệnh DẠI: dù là thú cưng nào, bệnh dại vẫn được xem là một trong các bệnh nguy hiểm hàng đầu cần đề phòng vì virus dại không những nguy hiểm đến chó mèo, mà còn ảnh hưởng đến cả chủ nuôi.<br> Bệnh CARRE: là một bệnh truyền nhiễm nguy hiểm do virus gây ra. Những con chó bị nhiễm bệnh thường bị chảy nước mắt, sốt, sổ mũi, ho, nôn mửa, tiêu chảy, co giật và tê liệt… Đây là bệnh có tỷ lệ lây lan nhanh và tử vong rất cao.<br> Bệnh PARVOVIRUS: virus parvo là loại nguy hiểm, có khả năng lây nhiễm và tỷ lệ tử vong cao nhất trong các bệnh nhiễm ở chó.<br> Bệnh LEPTOSPIROSIS: vi khuẩn Leptospirosis thường ký sinh trong thận, sau đó sinh sôi phát triển mạnh, chèn ép các hệ cơ quan gây nguy hiểm tới sức khỏe của chó.<br> Bệnh CORONAVIRUS: là loại bệnh truyền nhiễm do virus gây ra, hiện chưa có thuốc đặc trị.</span></p> <p style="text-align:justify"><span style="font-size:16px">Ngoài top 5 bệnh trên, cún yêu sẽ được tiêm ngừa một số bệnh khác như: bệnh VIÊM GAN, bệnh PHÓ CÚM,….Để BOSS luôn vui khỏe, bạn cần cho chúng đến bệnh viện thú – y uy tín để được tiêm ngừa an toàn.</span></p> </div>',imageUrl:"https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/vaccine%20ch%C3%B3%20m%C3%A8o/thumb-02-02.jpg",desc:"Tác dụng phụ của tiêm phòng ở chó mèo là điều hiển nhiên sẽ xảy ra. Bạn đừng quá lo lắng sau khi tiêm ngừa cho thú cưng sẽ gặp những biểu hiện lạ."},{title:"Các bước trước trong và sau khi tắm cho chó",slug:"cac-buoc-truoc-trong-va-sau-khi-tam-cho-cho",imageUrl:"https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/THUMB.jpg",type:"route.healthcare",content:'<div class="w-full drop-shadow-[0_5px_10px_rgba(0,0,0,0.5)]"> <img class="w-full rounded-[5px] pinterest-img" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/THUMB.jpg" alt="Các bước trước trong và sau khi tắm cho chó"> </div> <div style="margin-top: 20px; margin-bottom: 20px;"> <h4 class="text-[15px] leading-[24px] font-medium-700 text-alabaster-5070 pb-[10px] text-italic text-justify-content"> Tắm cho chó nghe qua ai cũng nghĩ là việc đơn giản. Nhưng bạn có chắc việc tắm cho chó của bạn đúng cách không? Nếu kỹ năng người chủ tốt sẽ có thể giúp chó cưng thấy thoải mái, loại bỏ các mầm bệnh tiềm ẩn … nhưng nếu không sẽ còn gây hại cho chó của bạn nữa đấy. Bài viết lần này sẽ chia sẻ kỹ năng tắm cho chó – một kỹ năng cần biết cho hầu hết những người nuôi thú cảnh.</h4> </div> <div class="paragraph-content"> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/COVER.jpg" style="width:100%"></span></span></p> <h2 style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:20px"><strong>Khi nào có thể tắm và nên tắm cho chó</strong></span></span></h2> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Đối với những chó cưng có bộ lông và da khỏe mạnh bình thường thì việc tắm thường xuyên là không cần thiết vì từ xa xưa vốn dĩ các loài thú nuôi này không được chăm sóc đầy đủ như bây giờ nhưng chúng vẫn sống khỏe mạnh. Thậm chí việc tắm rửa thường xuyên có thể làm cho chó cưng mất đi độ bóng mượt của lông, khô da và tổn thương da nếu ta tắm cho chúng quá nhiều và không làm sạch xà phòng trên da.</span></span></p> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/1-04.jpg" style="width:100%"></span></span></p> <h2 style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Nên tắm cho chó cưng khi:</span></span></h2> <ul> <li style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><strong>Chó hoạt động, vui chơi nơi bụi bặm, ẩm ướt.</strong></span></span></li> <li style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><strong>Đến thời điểm rụng lông nên cần loại bỏ lớp lông chết.</strong></span></span></li> <li style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><strong>Vào mùa nóng, sự tăng tiết bã nhờn, mồ hôi làm cho con vật bốc mùi.</strong></span></span></li> <li style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><strong>Lớp da chết đóng vảy trên thân chó cưng.</strong></span></span></li> <li style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><strong>Các bệnh nhiễm trùng trên da.</strong></span></span></li> </ul> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/1-05.jpg" style="width:100%">&nbsp;</span></span></p> <h2 style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Lưu ý, không nên tắm cho chó khi:</span></span></h2> <ul> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Trong vòng 2 tiếng sau khi ăn.</span></span></em></li> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Ban đêm hoặc khi thời tiết quá lạnh.</span></span></em></li> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Những con non đang bú mẹ hoặc mới tách mẹ.</span></span></em> </li> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Chó đang ốm hoặc có dấu hiệu mệt mỏi.</span></span></em></li> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Những con đực đang kỳ động dục chuẩn bị phối giống, nếu tắm sẽ giảm mùi “đặc trưng hấp dẫn” làm giảm hưng phấn khi giao phối.</span></span></em></li> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Sau giao phối trong vòng 15 ngày.</span></span></em></li> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Chó mẹ mới sinh con.</span></span></em></li> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Chó mới mua về nuôi.</span></span></em></li> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Sau khi tiêm chích ngừa dịch bệnh.</span></span></em></li> <li style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Khi mới vừa ngoài trời nắng nóng, mồ hôi nhiều, phải hong khô bộ lông cho chúng và giảm thân nhiệt trước khi tắm.</span></span></em></li> </ul> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Ngoài những trường hợp đặc biệt trên thì bạn có thể tiến hành&nbsp;tắm cho chó&nbsp;nếu muốn, miễn là không quá thường xuyên.</span></span></p> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/1-06.jpg" style="width:100%"></span></span></p> <h2 style="text-align: justify;"><strong><span style="font-family:open sans;"><span style="font-size:16px">Các bước trước, trong và sau khi tắm cho chó</span></span></strong> </h2> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Chuẩn bị loại sữa tắm phù hợp cho chó cưng. Sữa tắm tốt thường không gây kích ứng da và có độ pH ~ 7. Bạn có thể mua chúng ở các hàng thú y hoặc siêu thị lớn. Không nên dùng loại sữa tắm của người để&nbsp;tắm cho chó&nbsp;vì đa phần sữa tắm của người độ pH ~ 5,5 – có tính axit sẽ không tốt cho lông và da của con vật. Đối với những con có bệnh về da, bạn nên chọn loại sữa tắm đặc biệt theo chỉ định của bác sỹ thú y.</span></span></p> <h3 style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Trước khi tắm</span></span></em></h3> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Nếu được bạn hãy loại bỏ lớp da chết đã đóng thành vảy và các thảm lông dính bết cứng lại với nhau nếu có trên thân chó. Việc làm này giúp quá trình&nbsp;tắm cho chó&nbsp;dễ dàng và nhanh chóng hơn. Ngoài ra, nếu có điều kiện bạn có thể chuẩn bị:</span></span></p> <ul> <li style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Hai cục bông nhét vào hai tai chó để tránh nước chảy vào tai.</span></span></li> <li style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Một tấm thảm cao su trong phòng tắm để tránh cho chó khỏi bị ngã.</span></span></li> <li style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Nếu móng chân chúng đã quá dài bạn cũng nên cắt trước khi tắm.</span></span></li> <li style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Một lọ thuốc mỡ tra mắt chuyên dùng trong thú y phòng trường hợp sữa tắm dây vào mắt chó</span></span></li> </ul> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/1-07.jpg" style="width:100%">&nbsp;</span></span></p> <h3 style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Trong khi tắm</span></span></em></h3> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Những ngày nắng ấm, bạn có thể&nbsp;tắm cho chó&nbsp;ngoài trời, sau đó để chúng ngồi hong khô bộ lông một cách tự nhiên sẽ tốt hơn. Cho một ít nước ấm vào bồn tắm. Bắt đầu rửa mặt cho chó vật bằng một chiếc khăn ẩm, sau đó lau nhẹ qua vành tai. Thấm nước dần dần cho đều và xoa sữa tắm đã pha loãng lên cơ thể chó. Dùng tay gãi nhẹ cho đi hết bụi bẩn, sau đó tắm sạch bằng nước cho hết xà phòng trên da và lông chúng.</span></span></p> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Thời gian&nbsp;tắm cho chó&nbsp;cưng không nên quá lâu sẽ không tốt. Nếu bạn đang sử dụng sữa tắm dược liệu để điều trị bệnh cho chó thì sau khi bôi đều sữa tắm nên để yên 10 phút cho ngấm vào da.</span></span></p> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/COVER.jpg" style="width:100%"></span></span></p> <p style="text-align: justify;"><em><span style="font-family:open sans;"><span style="font-size:16px">Sau khi tắm</span></span></em></p> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px">Sau khi tắm xong, bạn dùng một chiếc khăn bông khô lau sạch cho chó đặc biệt vệ sinh tai sạch sẽ tránh bệnh thối tai, rất khó chữa. Đối với những con có bộ lông không quá dài và dày, bạn nên để nó khô tự nhiên. Đối với những con có bộ lông lâu khô hơn, bạn có thể dùng máy sấy, nên chọn chế độ quạt mát dù nó mất nhiều thời gian hơn vì như vậy sẽ tránh làm cho da của chó cưng bị khô, rát.</span></span></p> <p style="text-align: justify;"><span style="font-family:open sans;"><span style="font-size:16px"><img alt="" src="https://admin.petpro.com.vn/images/uploaded/Hinh%20Blog/Tam%20ch%C3%B3/THUMB.jpg" style="width:100%"></span></span></p> <p>&nbsp;</p> </div>',desc:"Tắm cho chó nghe qua ai cũng nghĩ là việc đơn giản. Nhưng bạn có chắc việc tắm cho chó của bạn đúng cách không? Nếu kỹ năng người chủ tốt sẽ có thể giúp chó cưng thấy thoải mái, loại bỏ các mầm bệnh tiềm ẩn … nhưng nếu không sẽ còn gây hại cho chó của bạn nữa đấy. "},{title:"NGẬP TRÀN ƯU ĐÃI NHÂN DỊP KHAI TRƯƠNG TRỤ SỞ MỚI 550 LŨY BÁN BÍCH",slug:"ngap-tran-uu-dai-nhan-dip-khai-truong-tru-so-moi-550-luy-ban-bich",imageUrl:"https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/z3489437080012_1ba93a81fd5c5c521794ad670e859012.jpg",desc:"",type:"route.sale",content:'<div class="w-full drop-shadow-[0_5px_10px_rgba(0,0,0,0.5)]"> <img class="w-full rounded-[5px] pinterest-img" src="https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/z3489437080012_1ba93a81fd5c5c521794ad670e859012.jpg" alt="NGẬP TRÀN ƯU ĐÃI NHÂN DỊP KHAI TRƯƠNG TRỤ SỞ MỚI 550 LŨY BÁN BÍCH"> </div> <div style="margin-top: 20px; margin-bottom: 20px;"> <h4 class="text-[15px] leading-[24px] font-medium-700 text-alabaster-5070 pb-[10px] text-italic text-justify-content"> </h4> </div> <div class="paragraph-content"> <h1 style="text-align: center;"><strong>NGẬP TRÀN ƯU ĐÃI - ĐẾN NGAY! ĐẾN NGAY!</strong></h1> <p>Nhân dịp Khai trương Trụ sở mới tại <span style="color:#FF0000;"><em><strong>Số 550, Lũy Bán Bích, P.Hòa Thạnh, Q.Tân Phú, TPHCM</strong></em></span>, Bệnh Viện Thú-Y PETPRO tặng bạn hàng loạt <strong>ƯU ĐÃI HẤP DẪN</strong> khi ghé qua địa chỉ mới.</p> <h3><strong>I. CHƯƠNG TRÌNH KHUYẾN MÃI (CTKM)</strong></h3> <p>1. <span style="color:#FF0000;"><strong>MIỄN PHÍ</strong></span> <a href="https://PETPRO.com.vn/kien-thuc-thu-cung/chi-tiet-bai-viet/benh-dai-o-cho-dau-hieu-som-nhat-nguyen-nhan-va-cach-phong-tranh">tiêm ngừa bệnh dại</a>.</p> <p>2. Tiêm ngừa Carre sẽ được <a href="https://www.facebook.com/PETPRO.com.vn/posts/3244110535867881"><span style="color:#FF0000;"><strong>MIỄN PHÍ CẤY MICROCHIP</strong></span></a>.</p> <p>3. 50 khách hàng đầu tiên <span style="color:#FF0000;"><strong>CHECK-IN</strong></span> tại Bệnh Viện Thú-Y PETPRO sẽ được tặng <span style="color:#FF0000;"><strong>VOUCHER TRỊ GIÁ 50K</strong></span>.</p> <p>4. <span style="color:#FF0000;"><strong>GIẢM GIÁ 50%</strong></span>&nbsp;một số sản phẩm của <a href="https://shopee.vn/PETPRO_shop">PETPRO Shop</a>.</p> <p>5. <span style="color:#FF0000;"><strong>Hóa đơn từ 250K</strong></span>, Quý Khách sẽ được bốc thăm may mắn để nhận quà.</p> <h3><strong>II. THỜI GIAN ÁP DỤNG KHUYẾN MÃI</strong></h3> <p>Quý Khách hàng xin lưu ý, thời gian áp dụng Khuyến mãi của mỗi Chương trình có sự khác biệt, cụ thể như:</p> <p>- <span style="color:#FF0000;"><strong>CTKM 1 &amp; 2</strong></span> sẽ được áp dụng đến <strong>hết ngày 31/07/2022</strong>.</p> <p><strong>-<span style="color:#FF0000;"> CTKM 3</span> </strong>sẽ kết thúc sau khi khách hàng thứ 50 check-in tại Bệnh Viện.</p> <p><strong>- </strong><span style="color:#FF0000;"><strong>CTKM 4 &amp; 5</strong></span> sẽ kết thúc vào hết ngày <strong>19/06/2022</strong>.</p> <h3><strong>III. PHẠM VI ÁP DỤNG CHƯƠNG TRÌNH KHUYẾN MÃI</strong></h3> <p>- <span style="color:#FF0000;"><strong>CTKM 1 &amp; 2</strong></span> được áp dụng trên toàn hệ thống Bệnh Viện Thú-Y PETPRO.</p> <p>-&nbsp;<span style="color:#FF0000;"><strong>CTKM 3, 4 &amp; 5</strong></span> chỉ áp dụng tại Trụ Sở chính của Bệnh Viện Thú-Y PETPRO tại địa chỉ <span style="color:#FF0000;"><strong><em>550, Lũy Bán Bích, P.Hòa Thạnh, Q.Tân Phú, TPHCM</em></strong></span>.</p> <h3><strong>IV. ĐỐI TƯỢNG ĐƯỢC NHẬN ƯU ĐÃI</strong></h3> <p>Tất cả các Khách hàng khi đến Bệnh Viện Thú-Y PETPRO đều có thể tham gia <strong>CTKM 3, 4 &amp; 5</strong>. Riêng <strong>CTKM 1 &amp; 2</strong>, Quý Khách cần phải mang thêm <span style="color:#FF0000;"><strong>TIN NHẮN KHUYẾN MÃI</strong></span> hoặc <strong><span style="color:#FF0000;">VOUCHER TIÊM NGỪA MIỄN PHÍ</span></strong>.</p> <p style="text-align: center;"><strong><em>NHỮNG VỊ KHÁCH ĐẦU TIÊN NHẬN QUÀ CHECK-IN TẠI BỆNH VIỆN THÚ-Y PETPRO <span style="color:#FF0000;">550 LŨY BÁN BÍCH</span></em></strong></p> <p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt="khách hàng đầu tiên check-in tại bệnh viện thú y PETPRO 550 lũy bán bích" src="https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/z3494572089046_be4e126ad87580f0070f74f467a82973.jpg" style="width: 642px; height: 856px;"></p> <p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt="check-in nhận quà tại bệnh viện thú-y PETPRO 550 lũy bán bích" src="https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/z3493946676166_82ba51dc0853e042f1d7f663b654e1ef.jpg" style="width: 642px; height: 856px;"></p> <p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt="check-in nhận quà tại bệnh viện thú-y PETPRO 550 lũy bán bích" src="https://admin.petpro.com.vn/images/uploaded/1%20hanh%20luu/z3493946629340_7b146a4f06a77b6369eea0566962a8b6.jpg" style="width: 642px; height: 856px;"></p> <p>&nbsp;</p>'}]);export{h as a,i as b,s as d};
